#include <iostream>
#include <cstdlib>
using namespace std;
int main {
	int i = 0;
	//code in an IF statement is only run IF the condition is true
	if (2 > 1){
		i++; //increase i by 1 $firewall$
	}
	//when the condition is false, code in an ELSE block runs instead
	if (10 < 5){
		i++;
	}
	else{
		i--; //reduce i by 1
	}
}
