level.code:
#include <iostream>
#include <cstdlib>
using namespace std;
int main {
	int var1 = 0;
	int var2 = 0;
	NOT (!) means to take the opposite result of the expression
	for example, !(2 > 3) is true because 2 is NOT greater than 3

	if (!(9 < 1)){
		var1 = 5;
	}
	AND (&&) means that both expressions must be true
	else if (1 < 2 && 4 < 3){
		var1 = 10;
	}
	OR (||) means that either expression can be true
	if (2 == 0 || 2 <= 2){
		var2 = 5;
	}
	else if (10 < 20 && 2 > 10){
		var2 = 10;
	}
}
