#include <iostream>
#include <cstdlib>
using namespace std;
int main {
	as you saw earlier, while loops can imitate for loops
	int i = 0;
	string text = "1";
	while (i < 5){
		text += "0";
		i++;
	}
	cout << text;
	//while loops continue if the condition is true
	If the condition is false before the loop starts, it NEVER runs.
	string word = "hello";
	i=0;
	while (i < 0){
		word += "o";
		i++;
	}
		//pay close attention to how the words change!
	cout << word;
