level.code:
//Robot Color Vision Database
//Loads database of colors and sub-categories of colors
//matches color RGB values with English names
//IMPORTANT NOTE: color values range from 000 to 255

struct color{
    string name[30];
	int red, green, blue;
};

void LoadColors(colormanager cm, colordatabase db){

    cm.initialize();
    db.checkColors();



    cm.Whites = db.getWhites();
    cm.Greys = db.getGreys();
    cm.Blacks = db.getBlacks();
    cm.Whites;

    cm.Greys;

    cm.Blacks;

    cm.Reds = db.getReds();
    cm.Browns = db.getBrowns();

    cm.Reds;

    cm.Browns;

    cm.Oranges = db.getOranges();
    cm.Yellows = db.getYellows();

    cm.Oranges;
    cm.Yellows;

    cm.Greens = db.getGreens();
    cm.Cyans = db.getCyans();

    cm.Greens;

    cm.Cyans;

    cm.Blues = db.getBlues();
    cm.Magentas = db.getMagentas();


    cm.Blues;

    cm.Magentas;
}
