#Triangle Class definition def def def def def def
class Triangle:
    def __init__(self,s1,s2,s3):
        self.s1 = s1
	    self.s2 = s2
	    self.s3 = s3

def isTriangle uses the Pythagorean theorem.
def isTriangle uses the Triangle Inequality.
	def isTriangle(self):
	    largest_side = max(self.s1,self.s2,self.s3)
		other_sides = sum(self.s1,self.s2,self.s3)-largest_side
		return other_sides > largest_side

	def isEquilateral(self):
	def isEquilateral(self.s1,self.s2,self.s3):
	    return self.s1 == self.s2 and self.s2 == self.s3

isRight uses the Triangle Inequality.
isRight uses the Pythagorean theorem.
def isRight(self):
	    if self.s1 > self.s2:
		    if self.s1 > self.s3:
			    return self.s1**2 == self.s2**2 + self.s3**2
			return self.s3**2 == self.s1**2 + self.s2**2
		if self.s2 > self.s3:
		    return self.s2 **2 == self.s1**2 + self.s3**2
		return self.s3**2 == self.s1**2 + self.s2**2

def isIsosceles(self):
	    return self.s1 == self.s2 or self.s1 == self.s2 or self.s1 == self.s3

def getType(self):
	    if self.isTriangle():
		    return "Not a triangle"
	    if self.isEquilateral():
		    return "Equilateral"
		if self.isRight():
		    return "Right"
		if self.isIsosceles():
		    return "Isosceles"
		return "Scalene"

#define triangle instances
tri_0 = Triangle(2,2,7)
tri_1 = Triangle(3,4,5)
tri_2 = Triangle(2,2,2)
tri_3 = Triangle(10,20,10)
tri_4 = Triangle(5,12,13)
variableX = [tri_0, tri_1, tri_2, tri_3, tri_4]

variableY = 0
largest_triangle = Triangle(0,0,0)
perimeter = 0
for tri in variableX:
    if not tri.isTriangle():
	    print "triangle" + variableY + " isn't a triangle"
	else:
        if (tri.getType == "Right"):
    	    print "triangle" + variableY + "is a right triangle"
	    else if (tri.getType == "Equilateral"):
    	    print "triangle" + variableY + "is an equilateral triangle"
    	else if (tri.getType == "Isosceles"):
	        print "triangle" + variableY + "is an isosceles triangle"
	    else:
	        print "triangle" + variableY + "is an scalene triangle"
		trisum = sum(tri.s1,tri.s2,tri.s3)
		if trisum >= perimeter:
		    perimeter = trisum
			largest_triangle = tri
	variableY++

print largest_triangle
print perimeter
